pragma solidity ^0.5.0;

import "@openzeppelin/contracts/access/Roles.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";

contract AssetTokenInstance {
    function balanceOf(address owner) public view returns (uint256);
    function allowance(address owner, address spender) public view returns (uint256);
    function transferFrom(address sender, address recipient, uint256 amount) public returns (bool);
}

contract Refund is Ownable {
    using Roles for Roles.Role;

    struct Transaction {
        string transactionType;
        address sender;
        address recipient;
        address tokenAddress;
        uint256 amount;
        bool refunded;
    }

    mapping(uint256 => Transaction) public transactions;
    uint256 totalTransactionsCount;

    Roles.Role assetTokenContracts;

    modifier onlyAssetTokenContracts() {
        require(assetTokenContracts.has(msg.sender), "Caller is not an authorized contract.");
        _;
    }

    function addAssetTokenContracts(address[] memory newContracts) public onlyOwner {
        for(uint i = 0; i < newContracts.length; i++) {
            assetTokenContracts.add(newContracts[i]);
        }
    }

    function addTransaction(
        string memory transactionType,
        address sender,
        address recipient,
        address tokenAddress,
        uint256 amount
    )
        public
        onlyAssetTokenContracts
        returns (uint256)
    {
        uint256 transactionId = totalTransactionsCount;

        Transaction memory transaction = Transaction({
            transactionType: transactionType,
            sender: sender,
            recipient: recipient,
            tokenAddress: tokenAddress,
            amount: amount,
            refunded: false
        });

        transactions[transactionId] = transaction;
        totalTransactionsCount += 1;
        return transactionId;
    }

    function refund(uint256 transactionId, address refundee, address assetTokenAddress)
        public
        returns (bool)
    {
        AssetTokenInstance a = AssetTokenInstance(assetTokenAddress);
        Transaction storage transaction = transactions[transactionId];

        require(
            a.balanceOf(msg.sender) >= transaction.amount,
            "Refunder does not have enough tokens."
        );
        require(
            a.allowance(msg.sender, address(this)) >= transaction.amount,
            "Contract not approved to send tokens on sender's behalf."
        );
        require(
            transaction.sender == refundee,
            "Refundee is not the sender of original transaction."
        );
        require(
            transaction.recipient == msg.sender,
            "Refunder is not the recipient of original transaction."
        );
        require(
            transaction.tokenAddress == assetTokenAddress,
            "Attempting to call refund on the wrong asset contract."
        );
        require(
            transaction.refunded == false,
            "Transaction has already been refunded."
        );

        a.transferFrom(msg.sender, refundee, transaction.amount);
        transaction.refunded = true;
        return true;
    }
}
